INSERT INTO `news` (`id`, `title`, `content`, `image`, `created_at`, `updated_at`) VALUES
(8, 'Da mụn đừng vội lo lắng \"Cách chăm sóc da mụn hiệu quả\"', '<div class=\"clearfix\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div class=\"hometext m-bottom\" style=\"box-sizing: border-box; font-weight: 700; margin-bottom: 10px !important;\">Muốn chăm sóc da mụn tốt đầu tiên bạn phải hiểu rõ làn da của mình trước đã. Cũng như muốn điều trị mụn trước hết bạn cần biết mụn từ đâu mà có, do đâu mà hình thành và phát triển? Tại sao tình trạng mụn của bạn lại không dứt lại ngày càng nặng hơn? Bài viết này sẽ giúp bạn tìm ra cách chăm sóc da hợp lý cho làn da của bạn, giúp cải thiện tình trạng mụn.</div>\r\n<img alt=\"Da mụn đừng vội lo lắng &quot;Cách chăm sóc da mụn hiệu quả&quot;\" class=\"img-thumbnail\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua.jpg\" style=\"background-color:transparent; border-radius:0px; border:none; box-sizing:border-box; display:inline-block; height:auto; line-height:1.42857; margin-right:5px; max-width:100%; padding:0px; transition:all 0.2s ease-in-out 0s; vertical-align:middle; width:460px\" />\r\n<p>Da mụn đừng vội lo lắng \"Cách chăm sóc da mụn hiệu quả\"</p>\r\n</div>\r\n\r\n<div class=\"danh-sach-box\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-bottom: 20px; height: 380px;\">&nbsp;</div>\r\n\r\n<div class=\"descriptionhtml js-toc-content\" id=\"news-bodyhtml\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\"><span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Muốn chăm sóc da mụn tốt đầu tiên bạn phải hiểu rõ làn da của mình trước đã. Cũng như muốn điều trị mụn trước hết bạn cần biết mụn từ đâu mà có, do đâu mà hình thành và phát triển? Tại sao tình trạng mụn của bạn lại không dứt lại ngày càng nặng hơn? Bài viết này sẽ giúp bạn tìm ra cách chăm sóc da hợp lý cho làn da của bạn, giúp cải thiện tình trạng mụn.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 3\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-3.jpg\" style=\"border:0px; box-sizing:border-box; height:400px; margin-right:5px; vertical-align:middle; width:600px\" /></div>\r\n&nbsp;\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>I. Nguyên nhân gây ra mụn</strong></span></span></span></h2>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Da của bạn thuộc da gì? Da dầu, da khô, da thường, da hỗn hợp. Làn da của chúng ta rất dễ bị ảnh hưởng từ những tác động bên ngoài, khói bụi, ô nhiễm môi trường, ánh nắng… Tất cả những tác nhân này đều dẫn đến bít tắc lỗ chân lông và gây ra mụn trứng cá, mụn đầu đen, mụn bọc, mụn sưng, mụn mủ. Nguyên nhân dẫn đến mụn do đâu?</span></span><br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>1.1 Sử dụng sản phẩm chăm sóc da không phù hợp</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Phổ biến nhất là kem dưỡng da do sản phẩm này có nhiều loại kết cấu khác nhau phù hợp với từng loại da. Ví dụ như khi bạn thuộc da dầu nhưng lại chọn loại kem dành cho da khô. Da dầu cần những sản phẩm dưỡng mỏng nhẹ thấm nhanh tránh gây bí tắc. Kem dành cho da khô thường có kết cấu đặc, độ ẩm cao, lâu thấm. Vì vậy nó nằm trên bề mặt da, cùng với dầu tự nhiên do da tiết ra trở thành một lớp màn khiến da “ngộp thở”. Để an toàn, bạn nên dùng những loại mỹ phẩm có ghi “Non comedogenic” thì sẽ không chứa các thành phần gây bí tắc da.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 1\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-1.jpg\" style=\"border:0px; box-sizing:border-box; height:350px; margin-right:5px; vertical-align:middle; width:512px\" /></div>\r\n&nbsp;\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>1.2 Không vệ sinh cọ trang điểm</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Các cô nàng tuổi trưởng thành khi đi làm hầu hết đều trang điểm hàng ngày. Cọ trang điểm chính là nơi tích tụ rất nhiều tế bào chết, kem phấn trang điểm cùng vi khuẩn. Hãy thử tưởng tượng mỗi lần dùng cọ là mỗi lần bạn đưa vi khuẩn lên mặt. Vì vậy hãy chú ý vệ sinh cọ và các dụng cụ trang điểm thường xuyên.</span></span><br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>1.3 Các vật dụng tiếp xúc với da mặt</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Tất cả những thứ tiếp xúc với da mặt phải đảm bảo sạch sẽ để không gây mụn. Bạn biết điều này nhưng vẫn có những thứ “vô tình” đưa lên mặt mà bạn không để ý tới. Ví dụ như điện thoại. Với hàng tỉ vi khuẩn lúc nhúc, điện thoại dơ hơn bạn tưởng nhiều. Hoặc khi bạn chống cằm thì cũng đã đưa vi khuẩn từ tay lên mặt. Vì vậy, hãy chú ý nhiều hơn và đảm bảo giữ vệ sinh cho da mặt.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 2\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-2.jpg\" style=\"border:0px; box-sizing:border-box; height:341px; margin-right:5px; vertical-align:middle; width:512px\" /></div>\r\n&nbsp;\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>1.4 Thức khuya và có chế độ ăn uống không phù hợp</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Điều này có rất nhiều người biết nhưng vẫn không thể thực hiện được chế độ sinh hoạt và ăn uống lành mạnh. Sau 11h đêm là thời gian cơ thể bắt đầu đào thải độc tố. Nếu bạn bỏ qua khoảng thời gian này thì lượng độc tố trong cơ thể không được đào thải gây nên mụn. Bên cạnh đó, tất cả những đồ ăn đồ uống cay nóng, nhiều đường, nhiều dầu mỡ và chứa chất kích thích đều khiến da tiết nhiều dầu hơn. Đó là lí do tại sao da bạn đã mụn lại càng mụn.</span></span><br />\r\n&nbsp;\r\n<h2><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>II.</strong>&nbsp;<strong>Cách chăm sóc da mụn hiệu quả</strong></span></span></span></h2>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>2.1 Rửa mặt 2 lần/ngày</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Để bảo vệ da nhờn mụn trước các vi khuẩn và bụi bẩn thì đầu tiên là bạn cần rửa mặt đúng cách thường xuyên mỗi ngày hai lần với sữa rửa mặt. Nên rửa mặt bằng nước ấm tránh dùng nước nóng khiến da bị khô. Khi rửa tránh chà xát quá mạnh trên da có thể khiến da bị tổn thương. Nguyên nhân gây mụn không chỉ là do da nhờn và bẩn nên không phải cứ rửa mặt thường xuyên là sẽ không có mụn.</span><br />\r\n<span style=\"font-family:calibri,sans-serif\">Bạn cũng nên rửa sạch da mặt sau khi tập thể dục hoặc hoạt động mạnh tạo ra nhiều mồ hôi. Do mồ hôi có thể kích thích mụn trứng cá phát triển. Nếu không có sẵn nước và sữa rửa mặt thì bạn có thể dùng khăn ướt để lau mặt sau khi hoạt động mạnh. Tuy nhiên nên ưu tiên sử dụng các loại khăn ướt không mùi để tránh gây kích ứng da.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 4\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-4.jpg\" style=\"border:0px; box-sizing:border-box; height:338px; margin-right:5px; vertical-align:middle; width:512px\" /></div>\r\n<br />\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Khi lựa chọn sữa rửa mặt cho da mụn bạn nên chọn là loại tẩy rửa nhẹ nhàng có độ pH 5 – 6.5. Những loại sữa rửa mặt có nhiều chất tẩy rửa có thể kích hoạt da tăng sản xuất dầu nhờn hơn. Các loại sữa rửa mặt thường không có ích trong việc giảm bớt dầu nhờn trên da thì bạn có thể thử các loại có chứa axit như bezoyl peroxide, axit salicylic, axit glycolic hoặc axit beta-hydroxy. Các loại sữa rửa mặt này rất phù hợp với da nhờn bị mụn lâu năm. Nhưng bạn nên mua một tuýp nhỏ dùng thử xem da có bị kích ứng không do trong các thành phần này có thể gây kích ứng trên da.</span></span><br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>2.2 Dùng nước hoa hồng</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Nước hoa hồng giúp kiểm soát dầu thừa trên da và thu nhỏ lỗ chân lông tạm thời nên người có da nhờn mụn lỗ chân lông to rất nên dùng. Tuy nhiên bạn nên dùng nước hoa hồng trên các vùng da nhiều dầu nhờn hơn như vùng chữ T, tránh các vùng da khô khác trên mặt.</span><br />\r\n<span style=\"font-family:calibri,sans-serif\">Bạn có thể thấm nước hoa hồng vào bông tẩy trang rồi lau lại da mặt và cổ. Bước chăm sóc này nên áp dụng sau khi rửa mặt nhưng trước khi dùng kem dưỡng ẩm hoặc bôi kem trị mụn trên da.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 5\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-5.jpg\" style=\"border:0px; box-sizing:border-box; height:900px; margin-right:5px; vertical-align:middle; width:600px\" /></div>\r\n<br />\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Những loại nước hoa hồng phù hợp với da mụn như: Nước hoa hồng Effaclar La Roche-Posay, nước hoa hồng trị mụn Caryophy, nước hoa hồng cho da mụn Meishoku…</span></span><br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>2.3 Dùng kem dưỡng da</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Đối với da nhờn mụn thì bạn nên ưu tiên chọn các loại kem dưỡng không chứa dầu. Nên ưu tiên các sản phẩm có chữ “FREE-Oil” trên kem dưỡng ẩm, kem chống nắng và đồ trang điểm. Bên cạnh đó, bạn cũng nên ưu tiên sản phẩm có nhãn NON-comedogenic (không gây bít lỗ chân lông) giúp giảm khả năng gây mụn.</span></span><br />\r\n&nbsp;\r\n<div style=\"box-sizing: border-box; text-align: center;\"><img alt=\"cach cham soc da mun hieu qua 6\" src=\"https://cocoshop.vn/uploads/news/2020_12/cach-cham-soc-da-mun-hieu-qua-6.jpg\" style=\"border:0px; box-sizing:border-box; height:400px; margin-right:5px; vertical-align:middle; width:600px\" /></div>\r\n&nbsp;\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>2.4 Sử dụng kem bôi trị mụn cho da nhờn mụn</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Da nhờn mụn và lỗ chân lông to thường đi đôi với nhau. Sử dụng kem bôi trị mụn cho da cũng có thể cải thiện lỗ chân lông to của bạn. Dù bạn khó có thể thu nhỏ lỗ chân lông như ban đầu nhưng bạn có thể khiến chúng trở nên nhỏ hơn một chút.</span><br />\r\n<span style=\"font-family:calibri,sans-serif\">Có thể sử dụng thuốc trị mụn có chứa: Retinoids, axit salicylic, axit alpha hydroxy (AAH) dạng gel. Do dạng gel sẽ khiến da đỡ cảm giác nặng và gây ra dầu thừa trên da nhờn mụn.</span><br />\r\n<span style=\"font-family:calibri,sans-serif\">Các loại kem bôi vừa trị mụn vừa thu nhỏ lỗ chân lông khi có chứa thành phần: Retinoids và axit salicylic giúp giảm thiểu lỗ chân lông nở to.</span></span><br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><span style=\"color:rgb(192, 57, 43)\"><span style=\"font-family:calibri,sans-serif\"><strong>2.5 Dùng giấy thấm dầu</strong></span></span></span></h3>\r\n<br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:calibri,sans-serif\">Giấy thấm dầu được xem là một giải pháp tuyệt vời để loại bỏ dầu trên da mà không gây khô da. Dùng giấy thấm dầu trên vùng da nhiều nhờn như trán, mũi, cằm. Nhưng bạn nên thấm nhẹ vào vùng da dầu trong 15 – 20 giây thay vì chà xát mạnh trên da dễ gây lây nhiễm mụn.</span><br />\r\n<span style=\"font-family:calibri,sans-serif\">Chăm sóc da mụn luôn cần sự tỉ mỉ và sự kiên trì, những cô nàng đang điều trị mụn đừng nên nóng vội. Chỉ cần bạn tuân thủ đúng theo những quy trình và sử dụng những sản phẩm phù hợp với làn da của mình sẽ giúp da bạn khỏe lên từng ngày và đánh bay các loại mụn.</span></span></div>', 'doxDX5h0_cach-cham-soc-da-mun-hieu-qua.jpg', '2021-01-02 14:38:31', '2021-01-02 14:38:31'),
(9, 'Hướng dẫn cách mix và layer các sản phẩm skincare', '<div class=\"clearfix\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div class=\"hometext m-bottom\" style=\"box-sizing: border-box; font-weight: 700; margin-bottom: 10px !important;\">\r\n<div class=\"clearfix\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div class=\"hometext m-bottom\" style=\"box-sizing: border-box; font-weight: 700; margin-bottom: 10px !important;\">Cách kết hợp các hoạt chất dưỡng da nhất định phải biết nếu không muốn skincare \"công dã tràng\". 2020 rồi, dưỡng da bây giờ không chỉ còn đơn giản là Rửa mặt – dưỡng ẩm – chống nắng nữa, mà còn là tầng tầng lớp lớp bao nhiêu là serum, bao nhiêu là đặc trị…</div>\r\n<img alt=\"Hướng dẫn cách mix và layer các sản phẩm skincare\" class=\"img-thumbnail\" src=\"https://cocoshop.vn/uploads/news/2020_11/cac-buoc-skincare.jpg\" style=\"background-color:transparent; border-radius:0px; border:none; box-sizing:border-box; display:inline-block; height:auto; line-height:1.42857; margin-right:5px; max-width:100%; padding:0px; transition:all 0.2s ease-in-out 0s; vertical-align:middle; width:460px\" />\r\n<p>Hướng dẫn cách mix và layer các sản phẩm skincare</p>\r\n</div>\r\n\r\n<div class=\"danh-sach-box\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-bottom: 20px; height: 156px;\">\r\n<div class=\"danh-sach\" style=\"box-sizing: border-box; border: 2px solid rgb(0, 0, 0); padding: 10px; background: rgb(243, 243, 243); margin-bottom: 20px; border-radius: 6px; right: -114.5px;\">\r\n<div class=\"head-danh-sach\" style=\"box-sizing: border-box; background: none; border: none; width: 791.5px; font-weight: 700; text-transform: uppercase; font-size: 16px; padding: 5px 0px;\">MỤC LỤC&nbsp;</div>\r\n\r\n<div class=\"toc js